import { createContext, useState, useEffect } from "react";
import axios from "axios";

export const AuthContext = createContext();
export const api = axios.create({ baseURL: "http://localhost:8000/api" });

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Carregar usuário do localStorage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const storedAccess = localStorage.getItem("access_token");
    const storedRefresh = localStorage.getItem("refresh_token");

    if (storedUser && storedAccess && storedRefresh) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (err) {
        console.error("Erro ao ler usuário do localStorage:", err);
        localStorage.clear();
        setUser(null);
      }
    }
    setLoading(false);
  }, []);

  // Login
  const login = async (email, password) => {
    try {
      const res = await api.post("/login/", { email, password });
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setUser(res.data.user);
      return res.data.user;
    } catch (err) {
      console.error(err.response?.data || err);
      throw new Error("Credenciais inválidas.");
    }
  };

  // Registro + login automático
  const register = async ({ nome, email, telefone, password }) => {
    try {
      await api.post("/register/", { nome, email, telefone, password });
      return await login(email, password);
    } catch (err) {
      console.error(err.response?.data || err);
      throw new Error("Erro ao registrar. Verifique os dados e tente novamente.");
    }
  };

  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  const refreshToken = async () => {
    const refresh = localStorage.getItem("refresh_token");
    if (!refresh) throw new Error("No refresh token available");

    try {
      const res = await api.post("/token/refresh/", { refresh });
      localStorage.setItem("access_token", res.data.access);
      return res.data.access;
    } catch (err) {
      logout();
      throw new Error("Sessão expirada. Faça login novamente.");
    }
  };

  // Interceptadores Axios
  useEffect(() => {
    const reqInterceptor = api.interceptors.request.use((config) => {
      const token = localStorage.getItem("access_token");
      if (token) config.headers.Authorization = `Bearer ${token}`;
      return config;
    });

    const resInterceptor = api.interceptors.response.use(
      (response) => response,
      async (error) => {
        const originalRequest = error.config;
        if (error.response?.status === 401 && !originalRequest._retry) {
          originalRequest._retry = true;
          try {
            const newAccess = await refreshToken();
            originalRequest.headers.Authorization = `Bearer ${newAccess}`;
            return api(originalRequest);
          } catch (err) {
            return Promise.reject(err);
          }
        }
        return Promise.reject(error);
      }
    );

    return () => {
      api.interceptors.request.eject(reqInterceptor);
      api.interceptors.response.eject(resInterceptor);
    };
  }, []);

  if (loading) return <div>Carregando...</div>;

  return (
    <AuthContext.Provider value={{ user, login, logout, register }}>
      {children}
    </AuthContext.Provider>
  );
};
