import React from "react";
import dayjs from "dayjs";

const ResourceViewer = ({ resource, turma }) => {
  const hoje = dayjs();

  // Verifica se o aluno pode acessar
  const dataInicio = dayjs(turma.data_inicio);
  const acessoPrevio = resource.acesso_previo;
  const isDraft = resource.draft;

  const podeAcessarAntes = hoje.isBefore(dataInicio) && acessoPrevio;
  const podeAcessarDepois = hoje.isAfter(dataInicio) && !isDraft;

  const temAcesso = podeAcessarAntes || podeAcessarDepois;

  if (!temAcesso) {
    return (
      <div className="p-4 border rounded bg-gray-100 text-gray-600">
        <h4>{resource.nome}</h4>
        <p>üîí Recurso indispon√≠vel no momento.</p>
      </div>
    );
  }

  // Renderiza√ß√£o conforme o tipo de recurso
  if (resource.tipo_recurso === "video") {
    return (
      <div className="p-4 border rounded shadow mb-3">
        <h4>{resource.nome}</h4>
        <video width="480" controls>
          <source src={resource.url} type="video/mp4" />
          Seu navegador n√£o suporta v√≠deo.
        </video>
        <div>
          <a href={resource.url} download>
            ‚¨áÔ∏è Download do v√≠deo
          </a>
        </div>
      </div>
    );
  }

  if (resource.tipo_recurso === "pdf") {
    return (
      <div className="p-4 border rounded shadow mb-3">
        <h4>{resource.nome}</h4>
        <iframe
          src={resource.url}
          width="480"
          height="600"
          title={resource.nome}
        ></iframe>
        <div>
          <a href={resource.url} download>
            ‚¨áÔ∏è Download do PDF
          </a>
        </div>
      </div>
    );
  }

  if (resource.tipo_recurso === "zip") {
    return (
      <div className="p-4 border rounded shadow mb-3">
        <h4>{resource.nome}</h4>
        <p>{resource.descricao}</p>
        <a href={resource.url} download>
          ‚¨áÔ∏è Baixar arquivo ZIP
        </a>
      </div>
    );
  }

  return (
    <div className="p-4 border rounded shadow mb-3">
      <h4>{resource.nome}</h4>
      <p>{resource.descricao}</p>
      <a href={resource.url} download>
        ‚¨áÔ∏è Download
      </a>
    </div>
  );
};

export default ResourceViewer;
