import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import api from "../api/api";
import AdminNavbar from "../components/AdminNavBar";
import { AuthContext } from "../context/AuthContext";

const TurmaDetalhe = () => {
  const { user } = useContext(AuthContext);
  const { id } = useParams();
  const [turma, setTurma] = useState(null);
  const [recursos, setRecursos] = useState([]);
  const [erro, setErro] = useState("");

  useEffect(() => {
    if (!user) return; // ðŸ‘ˆ Espera o usuÃ¡rio ser carregado antes de buscar

    const fetchTurma = async () => {
      try {
        const res = await api.get(`/turmas/${id}/detalhes/`);
        setTurma(res.data.turma);
        setRecursos(res.data.recursos);
      } catch (err) {
        console.error("Erro ao carregar turma:", err);
        if (err.response?.status === 403)
          setErro("Acesso negado a esta turma.");
        else if (err.response?.status === 401)
          setErro("SessÃ£o expirada. FaÃ§a login novamente.");
        else setErro("Erro ao buscar detalhes da turma.");
      }
    };

    fetchTurma();
  }, [id, user]); // ðŸ‘ˆ Recarrega quando o user estiver definido

  if (erro) return <div className="alert alert-danger">{erro}</div>;
  if (!turma) return <p>Carregando...</p>;

  return (
    <>
      <h2>OlÃ¡, {user?.nome}!</h2>
      <AdminNavbar />
      <div className="container py-4">
        <h2>{turma.nome}</h2>
        <p><b>Treinamento:</b> {turma.treinamento_nome}</p>
        <p><b>PerÃ­odo:</b> {turma.data_inicio} - {turma.data_conclusao}</p>

        {turma.meet_link && (
          <a
            href={turma.meet_link}
            target="_blank"
            rel="noopener noreferrer"
            className="btn btn-success mb-4"
          >
            Entrar no Google Meet
          </a>
        )}

        <h4>ðŸ“š Recursos da Turma</h4>
        <ul className="list-group">
          {recursos.map((r) => (
            <li key={r.id} className="list-group-item">
              <b>{r.nome}</b> - {r.descricao}
              {r.arquivo && (
                <a
                  href={r.arquivo}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-outline-primary btn-sm float-end"
                >
                  Abrir Arquivo
                </a>
              )}
              {r.url && (
                <a
                  href={r.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-outline-secondary btn-sm float-end"
                >
                  Abrir Link
                </a>
              )}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default TurmaDetalhe;
