// src/pages/admin/Turmas.js
import React, { useEffect, useState } from "react";
import api from "../../api/api";
import AdminNavbar from "../../components/AdminNavBar";

const Turmas = () => {
  const [turmas, setTurmas] = useState([]);
  const [treinamentos, setTreinamentos] = useState([]);
  const [form, setForm] = useState({
    nome: "",
    treinamento: "",
    data_inicio: "",
    data_conclusao: "",
  });

  const fetchTurmas = async () => {
    const res = await api.get("/turmas/");
    setTurmas(res.data);
  };

  const fetchTreinamentos = async () => {
    const res = await api.get("/treinamentos/");
    setTreinamentos(res.data);
  };

  useEffect(() => {
    fetchTreinamentos();
    fetchTurmas();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post("/turmas/cadastro/", form);
    setForm({ nome: "", treinamento: "", data_inicio: "", data_fim: "" });
    fetchTurmas();
  };

  return (
    <>
      <AdminNavbar />
      <div className="container py-4">
        <h2 className="mb-4">üè´ Cadastrar Turma</h2>

        <form onSubmit={handleSubmit} className="mb-5">
          <div className="row g-3">
            <div className="col-md-3">
              <input
                type="text"
                className="form-control"
                placeholder="Nome da Turma"
                value={form.nome}
                onChange={(e) =>
                  setForm({ ...form, nome: e.target.value })
                }
                required
              />
            </div>

            <div className="col-md-3">
              <select
                className="form-select"
                value={form.treinamento}
                onChange={(e) =>
                  setForm({ ...form, treinamento: e.target.value })
                }
                required
              >
                <option value="">Selecione o Treinamento</option>
                {treinamentos.map((t) => (
                  <option key={t.id} value={t.id}>
                    {t.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="col-md-2">
              <input
                type="date"
                className="form-control"
                value={form.data_inicio}
                onChange={(e) =>
                  setForm({ ...form, data_inicio: e.target.value })
                }
                required
              />
            </div>

            <div className="col-md-2">
              <input
                type="date"
                className="form-control"
                value={form.data_fim}
                onChange={(e) =>
                  setForm({ ...form, data_fim: e.target.value })
                }
                required
              />
            </div>

            <div className="col-md-2 d-grid">
              <button type="submit" className="btn btn-primary">
                Salvar
              </button>
            </div>
          </div>
        </form>

        <h3>Lista de Turmas</h3>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Treinamento</th>
                <th>Data In√≠cio</th>
                <th>Data Fim</th>
              </tr>
            </thead>
            <tbody>
              {turmas.map((t) => (
                <tr key={t.id}>
                  <td>{t.nome}</td>
                  <td>
                    {treinamentos.find((tr) => tr.id === t.treinamento)?.nome ||
                      "‚Äî"}
                  </td>
                  <td>{t.data_inicio}</td>
                  <td>{t.data_fim}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default Turmas;
