import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavBar";
import api from "../../api/api";
import { AuthContext } from "../context/AuthContext";

const Recursos = () => {
    const { user } = useContext(AuthContext);
  
  const [recursos, setRecursos] = useState([]);
  const [turmas, setTurmas] = useState([]);
  const [form, setForm] = useState({
    nome: "",
    descricao: "",
    tipo_recurso: "video",
    url: "",
    turma: "",
    arquivo: null,
  });

  // Buscar dados
  const fetchRecursos = async () => {
    try {
      const res = await api.get("/recursos/");
      setRecursos(res.data);
    } catch (error) {
      console.error("Erro ao buscar recursos:", error);
    }
  };

  const fetchTurmas = async () => {
    try {
      const res = await api.get("/turmas/");
      setTurmas(res.data);
    } catch (error) {
      console.error("Erro ao buscar turmas:", error);
    }
  };

  useEffect(() => {
    fetchTurmas();
    fetchRecursos();
  }, []);

  // Envio do formulário
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const data = new FormData();
      data.append("nome", form.nome);
      data.append("descricao", form.descricao);
      data.append("tipo_recurso", form.tipo_recurso);
      data.append("turma", form.turma);

      if (form.tipo_recurso === "video" || form.tipo_recurso === "link") {
        data.append("url", form.url);
      }

      if (form.arquivo) {
        data.append("arquivo", form.arquivo);
      }

      await api.post("/recursos/cadastro/", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setForm({
        nome: "",
        descricao: "",
        tipo_recurso: "video",
        url: "",
        turma: "",
        arquivo: null,
      });

      fetchRecursos();
    } catch (error) {
      console.error("Erro ao cadastrar recurso:", error);
    }
  };

  // Renderização
  return (
    <>
      <h2>Bem-vindo, {user?.nome}!</h2>

      <AdminNavbar />
      <div className="container py-4">
        <h2 className="mb-4">📂 Cadastrar Recurso</h2>

        <form onSubmit={handleSubmit} className="mb-5">
          <div className="row g-3">
            <div className="col-md-4">
              <input
                type="text"
                className="form-control"
                placeholder="Nome do Recurso"
                value={form.nome}
                onChange={(e) => setForm({ ...form, nome: e.target.value })}
                required
              />
            </div>

            <div className="col-md-4">
              <textarea
                className="form-control"
                placeholder="Descrição"
                value={form.descricao}
                onChange={(e) => setForm({ ...form, descricao: e.target.value })}
                rows={1}
              />
            </div>

            <div className="col-md-2">
              <select
                className="form-select"
                value={form.tipo_recurso}
                onChange={(e) => setForm({ ...form, tipo_recurso: e.target.value })}
              >
                <option value="video">Vídeo</option>
                <option value="pdf">PDF</option>
                <option value="zip">ZIP</option>
                <option value="link">Link</option>
                <option value="outro">Outro</option>
              </select>
            </div>

            {/* Campo URL */}
            {(form.tipo_recurso === "video" || form.tipo_recurso === "link") && (
              <div className="col-md-4">
                <input
                  type="url"
                  className="form-control"
                  placeholder="URL (ex: YouTube)"
                  value={form.url}
                  onChange={(e) => setForm({ ...form, url: e.target.value })}
                />
              </div>
            )}

            {/* Campo Upload */}
            {form.tipo_recurso !== "link" && (
              <div className="col-md-4">
                <input
                  type="file"
                  className="form-control"
                  accept={
                    form.tipo_recurso === "video"
                      ? "video/*"
                      : form.tipo_recurso === "pdf"
                      ? "application/pdf"
                      : form.tipo_recurso === "zip"
                      ? ".zip"
                      : "*/*"
                  }
                  onChange={(e) =>
                    setForm({ ...form, arquivo: e.target.files[0] })
                  }
                />
              </div>
            )}

            {/* Selecionar Turma */}
            <div className="col-md-4">
              <select
                className="form-select"
                value={form.turma}
                onChange={(e) => setForm({ ...form, turma: e.target.value })}
                required
              >
                <option value="">Selecione a Turma</option>
                {turmas.map((t) => (
                  <option key={t.id} value={t.id}>
                    {t.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="col-md-2 d-grid">
              <button type="submit" className="btn btn-primary">
                Salvar
              </button>
            </div>
          </div>
        </form>

        {/* LISTA DE RECURSOS */}
        <h3>📘 Lista de Recursos</h3>
        <div className="table-responsive">
          <table className="table table-striped align-middle">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Descrição</th>
                <th>Tipo</th>
                <th>Pré-visualização</th>
                <th>Turma</th>
              </tr>
            </thead>
            <tbody>
              {recursos.map((r) => (
                <tr key={r.id}>
                  <td>{r.nome}</td>
                  <td>{r.descricao || "—"}</td>
                  <td>{r.tipo_recurso}</td>
                  <td>
                    {/* VIDEO LOCAL */}
                    {r.tipo_recurso === "video" && r.arquivo ? (
                      <video
                        width="220"
                        height="140"
                        controls
                        src={r.arquivo}
                        style={{ borderRadius: "8px" }}
                      />
                    ) : r.tipo_recurso === "video" && r.url ? (
                      // VIDEO DO YOUTUBE
                      <div className="ratio ratio-16x9">
                        <iframe
                          src={r.url.replace("watch?v=", "embed/")}
                          title={r.nome}
                          allowFullScreen
                          style={{ borderRadius: "8px" }}
                        ></iframe>
                      </div>
                    ) : r.tipo_recurso === "pdf" && r.arquivo ? (
                      <a
                        href={r.arquivo}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="btn btn-outline-secondary btn-sm"
                      >
                        📄 Abrir PDF
                      </a>
                    ) : r.tipo_recurso === "zip" && r.arquivo ? (
                      <a
                        href={r.arquivo}
                        download
                        className="btn btn-outline-secondary btn-sm"
                      >
                        📦 Baixar ZIP
                      </a>
                    ) : r.tipo_recurso === "link" && r.url ? (
                      <a
                        href={r.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="btn btn-outline-primary btn-sm"
                      >
                        🔗 Acessar Link
                      </a>
                    ) : (
                      "—"
                    )}
                  </td>
                  <td>
                    {turmas.find((t) => t.id === r.turma)?.nome || "—"}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default Recursos;
