// src/pages/admin/Recursos.js
import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavBar";
import api from "../../api/api";

const Recursos = () => {
  const [recursos, setRecursos] = useState([]);
  const [turmas, setTurmas] = useState([]);
  const [form, setForm] = useState({ nome: "", descricao: "", tipo_recurso: "video", url: "", turma: "" });

  const fetchRecursos = async () => {
    const res = await api.get("/recursos/");
    setRecursos(res.data);
  };

  const fetchTurmas = async () => {
    const res = await api.get("/turmas/");
    setTurmas(res.data);
  };

  useEffect(() => {
    fetchTurmas();
    fetchRecursos();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post("/recursos/", form);
    setForm({ nome: "", descricao: "", tipo_recurso: "video", url: "", turma: "" });
    fetchRecursos();
  };

  return (
    <div className="p-4">
      <h2>ðŸ“‚ Cadastrar Recurso</h2>

      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nome" value={form.nome} onChange={(e) => setForm({ ...form, nome: e.target.value })} />
        <textarea placeholder="DescriÃ§Ã£o" value={form.descricao} onChange={(e) => setForm({ ...form, descricao: e.target.value })} />
        <select value={form.tipo_recurso} onChange={(e) => setForm({ ...form, tipo_recurso: e.target.value })}>
          <option value="video">VÃ­deo</option>
          <option value="pdf">PDF</option>
          <option value="link">Link</option>
          <option value="outro">Outro</option>
        </select>
        <input type="url" placeholder="URL" value={form.url} onChange={(e) => setForm({ ...form, url: e.target.value })} />
        <select value={form.turma} onChange={(e) => setForm({ ...form, turma: e.target.value })}>
          <option value="">Selecione a Turma</option>
          {turmas.map((t) => (
            <option key={t.id} value={t.id}>{t.nome}</option>
          ))}
        </select>
        <button type="submit">Salvar</button>
      </form>

      <h3>Lista de Recursos</h3>
      <ul>
        {recursos.map((r) => (
          <li key={r.id}>{r.nome} ({r.tipo_recurso})</li>
        ))}
      </ul>
    </div>
  );
};

export default Recursos;
