import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import api from "../api/api";
import { AuthContext } from "../context/AuthContext";
import AdminNavbar from "../components/AdminNavBar";

const TurmaDetalhe = () => {
  const { user } = useContext(AuthContext);
  const { id } = useParams();
  const [turma, setTurma] = useState(null);
  const [recursos, setRecursos] = useState([]);
  const [erro, setErro] = useState("");
  const [novoAluno, setNovoAluno] = useState("");
  const [novoRecurso, setNovoRecurso] = useState({ nome: "", descricao: "" });

  useEffect(() => {
    if (!user) return;
    const fetchTurma = async () => {
      try {
        const res = await api.get(`/turmas/${id}/detalhes/`);
        setTurma(res.data.turma);
        setRecursos(res.data.recursos);
      } catch (err) {
        setErro("Erro ao carregar turma.");
      }
    };
    fetchTurma();
  }, [id, user]);

  const handleAddAluno = async () => {
    try {
      await api.patch(`/turmas/${id}/editar/`, { alunos_ids: [parseInt(novoAluno)] });
      alert("Aluno adicionado com sucesso!");
      setNovoAluno("");
    } catch (err) {
      alert("Erro ao adicionar aluno");
    }
  };

  const handleAddRecurso = async () => {
    try {
      await api.patch(`/turmas/${id}/editar/`, { recursos: [novoRecurso] });
      alert("Recurso adicionado!");
      setNovoRecurso({ nome: "", descricao: "" });
    } catch (err) {
      alert("Erro ao adicionar recurso");
    }
  };

  if (!turma) return <p>Carregando...</p>;
  if (erro) return <div className="alert alert-danger">{erro}</div>;

  // ✅ Agora usamos turma.criado_por retornado do backend
  const isCriador = user && turma.criado_por === user.id;

  return (
    <div className="container py-4">
      <AdminNavbar />
      <h2>{turma.nome}</h2>
      <p><b>Treinamento:</b> {turma.treinamento}</p>
      <p><b>Período:</b> {turma.data_inicio} - {turma.data_conclusao}</p>

      {isCriador && (
        <div className="mt-4 p-3 border rounded">
          <h4>⚙️ Edição da Turma</h4>

          {/* Adicionar aluno */}
          <div className="mb-3">
            <label>ID do Aluno:</label>
            <input
              type="number"
              className="form-control"
              value={novoAluno}
              onChange={(e) => setNovoAluno(e.target.value)}
            />
            <button className="btn btn-primary mt-2" onClick={handleAddAluno}>
              ➕ Adicionar Aluno
            </button>
          </div>

          {/* Adicionar recurso */}
          <div className="mb-3">
            <label>Nome do Recurso:</label>
            <input
              type="text"
              className="form-control"
              value={novoRecurso.nome}
              onChange={(e) => setNovoRecurso({ ...novoRecurso, nome: e.target.value })}
            />
            <label>Descrição:</label>
            <input
              type="text"
              className="form-control"
              value={novoRecurso.descricao}
              onChange={(e) =>
                setNovoRecurso({ ...novoRecurso, descricao: e.target.value })
              }
            />
            <button className="btn btn-success mt-2" onClick={handleAddRecurso}>
              ➕ Adicionar Recurso
            </button>
          </div>
        </div>
      )}

      <h4 className="mt-4">📚 Recursos</h4>
      <ul className="list-group">
        {recursos.map((r) => (
          <li key={r.id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <b>{r.nome}</b> — {r.descricao}
            </div>
            <div>
              {r.arquivo && (
                <a
                  href={r.arquivo}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-outline-primary btn-sm me-2"
                >
                  📂 Abrir Arquivo
                </a>
              )}
              {r.url && (
                <a
                  href={r.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-outline-secondary btn-sm"
                >
                  🌐 Abrir Link
                </a>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TurmaDetalhe;
