// src/pages/admin/Recursos.js
import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavBar";
import api from "../../api/api";

const Recursos = () => {
  const [recursos, setRecursos] = useState([]);
  const [turmas, setTurmas] = useState([]);
  const [form, setForm] = useState({
    nome: "",
    descricao: "",
    tipo_recurso: "video",
    url: "",
    turma: "",
  });

  const fetchRecursos = async () => {
    const res = await api.get("/recursos/");
    setRecursos(res.data);
  };

  const fetchTurmas = async () => {
    const res = await api.get("/turmas/");
    setTurmas(res.data);
  };

  useEffect(() => {
    fetchTurmas();
    fetchRecursos();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post("/recursos/cadastro/", form);
    setForm({ nome: "", descricao: "", tipo_recurso: "video", url: "", turma: "" });
    fetchRecursos();
  };

  return (
    <>
      <AdminNavbar />
      <div className="container py-4">
        <h2 className="mb-4">沒 Cadastrar Recurso</h2>

        <form onSubmit={handleSubmit} className="mb-5">
          <div className="row g-3">
            <div className="col-md-4">
              <input
                type="text"
                className="form-control"
                placeholder="Nome do Recurso"
                value={form.nome}
                onChange={(e) => setForm({ ...form, nome: e.target.value })}
                required
              />
            </div>

            <div className="col-md-4">
              <textarea
                className="form-control"
                placeholder="Descriﾃｧﾃ｣o"
                value={form.descricao}
                onChange={(e) => setForm({ ...form, descricao: e.target.value })}
                rows={1}
              />
            </div>

            <div className="col-md-2">
              <select
                className="form-select"
                value={form.tipo_recurso}
                onChange={(e) => setForm({ ...form, tipo_recurso: e.target.value })}
              >
                <option value="video">Vﾃｭdeo</option>
                <option value="pdf">PDF</option>
                <option value="link">Link</option>
                <option value="outro">Outro</option>
              </select>
            </div>

            <div className="col-md-4">
              <input
                type="url"
                className="form-control"
                placeholder="URL"
                value={form.url}
                onChange={(e) => setForm({ ...form, url: e.target.value })}
              />
            </div>

            <div className="col-md-4">
              <select
                className="form-select"
                value={form.turma}
                onChange={(e) => setForm({ ...form, turma: e.target.value })}
                required
              >
                <option value="">Selecione a Turma</option>
                {turmas.map((t) => (
                  <option key={t.id} value={t.id}>
                    {t.nome}
                  </option>
                ))}
              </select>
            </div>

            <div className="col-md-2 d-grid">
              <button type="submit" className="btn btn-primary">
                Salvar
              </button>
            </div>
          </div>
        </form>

        <h3>Lista de Recursos</h3>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Descriﾃｧﾃ｣o</th>
                <th>Tipo</th>
                <th>URL</th>
                <th>Turma</th>
              </tr>
            </thead>
            <tbody>
              {recursos.map((r) => (
                <tr key={r.id}>
                  <td>{r.nome}</td>
                  <td>{r.descricao}</td>
                  <td>{r.tipo_recurso}</td>
                  <td>
                    {r.url ? (
                      <a href={r.url} target="_blank" rel="noopener noreferrer">
                        Link
                      </a>
