import { createContext, useState, useEffect } from "react";
import api from "../api/api"; // sua instância do axios

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // Recupera usuário do localStorage ao carregar
  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (stored) {
      try {
        setUser(JSON.parse(stored));
      } catch (err) {
        console.error("Erro ao ler usuário do localStorage:", err);
        localStorage.removeItem("user"); // remove valor inválido
      }
    }
  }, []);

  // Login
  const login = async (email, password) => {
    try {
      const res = await api.post("/login/", { email, password });

      // Salva token e usuário no localStorage
      localStorage.setItem("token", res.data.access);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setUser(res.data.user);

      return res.data.user;
    } catch (err) {
      console.error(err.response?.data || err);
      throw new Error("Erro ao fazer login. Verifique suas credenciais.");
    }
  };

  // Registro com login automático após criar usuário
  const register = async ({ nome, email, telefone, password }) => {
    try {
      // 1. Registrar usuário
      await api.post("/register/", { nome, email, telefone, password });
      console.log("Registro bem-sucedido");

      // 2. Login automático
      const res = await api.post("/login/", { email, password });
      localStorage.setItem("token", res.data.access);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setUser(res.data.user);

      return res.data.user;
    } catch (err) {
      console.error(err.response?.data || err);
      throw new Error("Erro ao registrar. Verifique os dados e tente novamente.");
    }
  };

  // Logout
  const logout = () => {
    localStorage.clear();
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, register }}>
      {children}
    </AuthContext.Provider>
  );
};
