// src/pages/admin/Turmas.js
import React, { useEffect, useState } from "react";
import api from "../../api/api";
import AdminNavbar from "../../components/AdminNavBar";

const Turmas = () => {
  const [turmas, setTurmas] = useState([]);
  const [treinamentos, setTreinamentos] = useState([]);
  const [form, setForm] = useState({ nome: "", treinamento: "", data_inicio: "", data_fim: "" });

  const fetchTurmas = async () => {
    const res = await api.get("/turmas/");
    setTurmas(res.data);
  };

  const fetchTreinamentos = async () => {
    const res = await api.get("/treinamentos/");
    setTreinamentos(res.data);
  };

  useEffect(() => {
    fetchTreinamentos();
    fetchTurmas();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await api.post("/turmas/", form);
    setForm({ nome: "", treinamento: "", data_inicio: "", data_fim: "" });
    fetchTurmas();
  };

  return (
    <div className="p-4">
      <h2>üè´ Cadastrar Turma</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nome" value={form.nome} onChange={(e) => setForm({ ...form, nome: e.target.value })} />
        <select value={form.treinamento} onChange={(e) => setForm({ ...form, treinamento: e.target.value })}>
          <option value="">Selecione o Treinamento</option>
          {treinamentos.map((t) => (
            <option key={t.id} value={t.id}>{t.nome}</option>
          ))}
        </select>
        <input type="date" value={form.data_inicio} onChange={(e) => setForm({ ...form, data_inicio: e.target.value })} />
        <input type="date" value={form.data_fim} onChange={(e) => setForm({ ...form, data_fim: e.target.value })} />
        <button type="submit">Salvar</button>
      </form>

      <h3>Lista de Turmas</h3>
      <ul>
        {turmas.map((t) => (
          <li key={t.id}>{t.nome} ‚Äî {t.data_inicio} at√© {t.data_fim}</li>
        ))}
      </ul>
    </div>
  );
};

export default Turmas;
